import React,{useState} from "react";
import { createChatBotMessage } from "react-chatbot-kit";
import volume from "../asset/volumeOn.png"


// const [selected, setSelected] = useState(volume);

const [lightMode, setLightMode ] = React.useState(false)
const config = {

  botName: "WeatherBot",
  initialMessages: [
    createChatBotMessage(`Hello! I am your weather assistant. How can I help ?`)
  ],
  customComponents: {
    // Replaces the default header

    header: () => <div style={{ backgroundColor: 'grey', padding: "5px" }}>
      WeatherBot

      {/* <button onClick={vidMute(flag)}> 
        <img src={volume} />
      </button> */}
      <img
              // className="mode-switch ml-auto"
              onClick={() => setLightMode(prevMode => !prevMode)}
              src={volume}

            />
    </div>
  }
};

export default config;
